import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { ArrowRight } from 'lucide-react';
import { Link } from 'wouter';
import { useLanguage } from '@/hooks/useLanguage';
import { getTranslation } from '@/lib/i18n';
import type { BlogPost } from '@shared/schema';

interface BlogCardProps {
  post: BlogPost;
}

export default function BlogCard({ post }: BlogCardProps) {
  const { language } = useLanguage();

  const getTitle = () => {
    switch (language) {
      case 'ka': return post.titleKa || post.title;
      case 'ru': return post.titleRu || post.title;
      default: return post.title;
    }
  };

  const getExcerpt = () => {
    switch (language) {
      case 'ka': return post.excerptKa || post.excerpt;
      case 'ru': return post.excerptRu || post.excerpt;
      default: return post.excerpt;
    }
  };

  const formatDate = (date: Date | null) => {
    if (!date) return '';
    return new Date(date).toLocaleDateString(language === 'ka' ? 'ka-GE' : language === 'ru' ? 'ru-RU' : 'en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const getCategoryColor = () => {
    switch (post.category.toLowerCase()) {
      case 'traditions': return 'bg-georgian-gold/20 text-georgian-wine';
      case 'heritage': return 'bg-georgian-terracotta/20 text-georgian-wine';
      case 'architecture': return 'bg-green-100 text-georgian-wine';
      default: return 'bg-georgian-gold/20 text-georgian-wine';
    }
  };

  return (
    <article className="bg-white rounded-xl shadow-lg overflow-hidden">
      <img
        src={post.imageUrl}
        alt={getTitle()}
        className="w-full h-48 object-cover"
      />
      <CardContent className="p-6">
        <div className="flex items-center mb-3">
          <Badge className={getCategoryColor()}>
            {post.category}
          </Badge>
          <span className="text-sm georgian-gray ml-3">
            {formatDate(post.publishedAt)}
          </span>
        </div>
        <h3 className="text-xl font-serif font-bold georgian-wine mb-3">
          {getTitle()}
        </h3>
        <p className="georgian-gray mb-4">
          {getExcerpt()}
        </p>
        <Link
          href={`/blog/${post.slug}`}
          className="georgian-wine hover:georgian-terracotta font-semibold transition-colors inline-flex items-center"
        >
          {getTranslation('common.readMore', language)}
          <ArrowRight className="ml-1 h-4 w-4" />
        </Link>
      </CardContent>
    </article>
  );
}

client/src/components/ContactForm.tsx.content:
import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent } from '@/components/ui/card';
import { useLanguage } from '@/hooks/useLanguage';
import { getTranslation } from '@/lib/i18n';
import { useMutation } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

export default function ContactForm() {
  const { language } = useLanguage();
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    interest: '',
    message: ''
  });

  const contactMutation = useMutation({
    mutationFn: async (data: typeof formData) => {
      return apiRequest('POST', '/api/contact', data);
    },
    onSuccess: () => {
      toast({
        title: "Message Sent",
        description: "Thank you for your message! We will get back to you soon.",
      });
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        interest: '',
        message: ''
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send message. Please try again.",
        variant: "destructive",
      });
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    contactMutation.mutate(formData);
  };

  const handleChange = (field: string, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  return (
    <Card className="bg-white rounded-xl shadow-lg">
      <CardContent className="p-8">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <Label htmlFor="firstName" className="block text-sm font-semibold georgian-wine mb-2">
                {getTranslation('contact.firstName', language)}
              </Label>
              <Input
                id="firstName"
                value={formData.firstName}
                onChange={(e) => handleChange('firstName', e.target.value)}
                className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-georgian-wine focus:border-transparent"
                required
              />
            </div>
            <div>
              <Label htmlFor="lastName" className="block text-sm font-semibold georgian-wine mb-2">
                {getTranslation('contact.lastName', language)}
              </Label>
              <Input
                id="lastName"
                value={formData.lastName}
                onChange={(e) => handleChange('lastName', e.target.value)}
                className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-georgian-wine focus:border-transparent"
                required
              />
            </div>
          </div>

          <div>
            <Label htmlFor="email" className="block text-sm font-semibold georgian-wine mb-2">
              {getTranslation('contact.email', language)}
            </Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) => handleChange('email', e.target.value)}
              className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-georgian-wine focus:border-transparent"
              required
            />
          </div>

          <div>
            <Label htmlFor="phone" className="block text-sm font-semibold georgian-wine mb-2">
              {getTranslation('contact.phone', language)}
            </Label>
            <Input
              id="phone"
              type="tel"
              value={formData.phone}
              onChange={(e) => handleChange('phone', e.target.value)}
              className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-georgian-wine focus:border-transparent"
            />
          </div>

          <div>
            <Label htmlFor="interest" className="block text-sm font-semibold georgian-wine mb-2">
              Area of Interest
            </Label>
            <Select value={formData.interest} onValueChange={(value) => handleChange('interest', value)}>
              <SelectTrigger className="w-full px-4 py-3 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-georgian-wine focus:border-transparent">
                <SelectValue placeholder="Select your interest" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="wine-tours">Wine Tours</SelectItem>
                <SelectItem value="culinary">Culinary Experiences</SelectItem>
                <SelectItem value="cultural">Cultural Heritage Tours</SelectItem>
                <SelectItem value="adventure">Adventure Tourism</SelectItem>
                <SelectItem value="custom">Custom Experience</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div>
