                      )}
                      <button
                        onClick={() => {
                          setIsMenuOpen(false);
                          logout();
                        }}
                        className="block w-full text-left px-3 py-2 text-base font-medium text-georgian-gray hover:text-georgian-wine"
                      >
                        <LogOut className="inline mr-2 h-4 w-4" />
