import { useState, useEffect } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Shield, Users, UserCheck, UserX, Crown, Map, Plane, Store, Edit, Trash2 } from "lucide-react";
import RegionEditForm from "@/components/RegionEditForm";
import { UserEditForm } from "@/components/UserEditForm";
import { TourEditForm } from "@/components/TourEditForm";
import { ProductEditForm } from "@/components/ProductEditForm";

interface User {
  id: string;
  username: string;
  email: string;
  firstName?: string | null;
  lastName?: string | null;
  role: string;
  isActive: boolean;
  createdAt: string;
}

interface Region {
  id: number;
  name: string;
  nameKa: string;
  nameRu: string;
  description: string;
  descriptionKa: string;
  descriptionRu: string;
  slug: string;
  featured: boolean;
  imageUrl: string;
}

interface Tour {
  id: number;
  title: string;
  titleKa: string;
  titleRu: string;
  description: string;
  descriptionKa: string;
  descriptionRu: string;
  price: number;
  duration: string;
  regionId: number;
  category: string;
  featured: boolean;
  imageUrl: string;
}

interface Product {
  id: number;
  name: string;
  nameKa: string;
  nameRu: string;
  description: string;
  descriptionKa: string;
  descriptionRu: string;
  price: number;
  category: string;
  featured: boolean;
  imageUrl: string;
  inStock: boolean;
}

export default function Admin() {
  const { user, isAdmin, isAuthenticated } = useAuth();
  const [, setLocation] = useLocation();
  const [activeTab, setActiveTab] = useState("users");
  const { toast } = useToast();

  // State for different data types
  const [users, setUsers] = useState<User[]>([]);
  const [regions, setRegions] = useState<Region[]>([]);
  const [tours, setTours] = useState<Tour[]>([]);
  const [products, setProducts] = useState<Product[]>([]);

  // Loading states
  const [usersLoading, setUsersLoading] = useState(true);
  const [regionsLoading, setRegionsLoading] = useState(false);
  const [toursLoading, setToursLoading] = useState(false);
  const [productsLoading, setProductsLoading] = useState(false);

  // Error states
  const [error, setError] = useState("");

  // Edit states
  const [editingRegion, setEditingRegion] = useState<Region | null>(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  const [editingUser, setEditingUser] = useState<User | null>(null);
  const [isUserEditModalOpen, setIsUserEditModalOpen] = useState(false);

  const [editingTour, setEditingTour] = useState<Tour | null>(null);
  const [isTourEditModalOpen, setIsTourEditModalOpen] = useState(false);

  const [editingProduct, setEditingProduct] = useState<Product | null>(null);
  const [isProductEditModalOpen, setIsProductEditModalOpen] = useState(false);

  // Redirect if not authenticated or not admin
  useEffect(() => {
    if (!isAuthenticated) {
      setLocation("/login");
      return;
    }

    if (!isAdmin) {
      setLocation("/");
      return;
    }
  }, [isAuthenticated, isAdmin, setLocation]);

  // Fetch users
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await fetch("/api/admin/users", {
          credentials: "include",
        });

        if (!response.ok) {
          throw new Error("Failed to fetch users");
        }

        const usersData = await response.json();
        setUsers(usersData);
      } catch (err) {
        const errorMessage = err instanceof Error ? err.message : "Failed to fetch users";
        setError(errorMessage);
        toast({
          title: "Error",
          description: errorMessage,
          variant: "destructive",
        });
      } finally {
        setUsersLoading(false);
      }
    };

    if (isAuthenticated && isAdmin) {
      fetchUsers();
    }
  }, [isAuthenticated, isAdmin, toast]);

  // Fetch regions
  const fetchRegions = async () => {
    if (!regionsLoading) {
      setRegionsLoading(true);
      try {
        const response = await fetch("/api/regions");
        if (!response.ok) throw new Error("Failed to fetch regions");
        const data = await response.json();
        setRegions(data);
      } catch (err) {
        toast({
          title: "Error",
          description: "Failed to fetch regions",
          variant: "destructive",
        });
      } finally {
        setRegionsLoading(false);
      }
    }
  };

  // Fetch tours
  const fetchTours = async () => {
    if (!toursLoading) {
      setToursLoading(true);
      try {
        const response = await fetch("/api/tours");
        if (!response.ok) throw new Error("Failed to fetch tours");
        const data = await response.json();
        setTours(data);
      } catch (err) {
        toast({
          title: "Error",
          description: "Failed to fetch tours",
          variant: "destructive",
        });
      } finally {
        setToursLoading(false);
      }
    }
  };

  // Fetch products
  const fetchProducts = async () => {
    if (!productsLoading) {
      setProductsLoading(true);
      try {
        const response = await fetch("/api/products");
        if (!response.ok) throw new Error("Failed to fetch products");
        const data = await response.json();
        setProducts(data);
      } catch (err) {
        toast({
          title: "Error",
          description: "Failed to fetch products",
          variant: "destructive",
        });
      } finally {
        setProductsLoading(false);
      }
    }
  };

  // Handle tab change and fetch data when needed
  const handleTabChange = (value: string) => {
    setActiveTab(value);
    if (value === "regions" && regions.length === 0) {
      fetchRegions();
    } else if (value === "tours" && tours.length === 0) {
      fetchTours();
    } else if (value === "products" && products.length === 0) {
      fetchProducts();
    }
  };

  // User management functions
  const updateUserRole = async (userId: string, role: string) => {
    try {
      const response = await fetch(`/api/admin/users/${userId}/role`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ role }),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to update user role");
      }

      const updatedUser = await response.json();
      setUsers(users.map(u => u.id === userId ? updatedUser : u));

      toast({
        title: "Success",
        description: `User role updated to ${role}`,
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to update role";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    }
  };

  const updateUserStatus = async (userId: string, isActive: boolean) => {
    try {
      const response = await fetch(`/api/admin/users/${userId}/status`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ isActive }),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to update user status");
      }

      const updatedUser = await response.json();
      setUsers(users.map(u => u.id === userId ? updatedUser : u));

      toast({
        title: "Success",
        description: `User ${isActive ? 'activated' : 'deactivated'}`,
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to update status";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
    }
  };

  // Region editing functions
  const handleEditRegion = (region: Region) => {
    setEditingRegion(region);
    setIsEditModalOpen(true);
  };

  const handleSaveRegion = async (regionId: number, data: any) => {
    try {
      const response = await fetch(`/api/admin/regions/${regionId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to update region");
      }

      const updatedRegion = await response.json();
      setRegions(regions.map(r => r.id === regionId ? updatedRegion : r));

      toast({
        title: "Success",
        description: "Region updated successfully",
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to update region";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
      throw err;
    }
  };

  const closeEditModal = () => {
    setIsEditModalOpen(false);
    setEditingRegion(null);
  };

  // User editing functions
  const handleEditUser = (user: User) => {
    setEditingUser(user);
    setIsUserEditModalOpen(true);
  };

  const handleSaveUser = async (userId: string, data: any) => {
    try {
      const response = await fetch(`/api/admin/users/${userId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to update user");
      }

      const updatedUser = await response.json();
      setUsers(users.map(u => u.id === userId ? updatedUser : u));

      toast({
        title: "Success",
        description: "User updated successfully",
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to update user";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
      throw err;
    }
  };

  const closeUserEditModal = () => {
    setIsUserEditModalOpen(false);
    setEditingUser(null);
  };

  // Tour editing functions
  const handleEditTour = (tour: Tour) => {
    setEditingTour(tour);
    setIsTourEditModalOpen(true);
  };

  const handleSaveTour = async (tourId: number, data: any) => {
    try {
      const response = await fetch(`/api/admin/tours/${tourId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to update tour");
      }

      const updatedTour = await response.json();
      setTours(tours.map(t => t.id === tourId ? updatedTour : t));

      toast({
        title: "Success",
        description: "Tour updated successfully",
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to update tour";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
      throw err;
    }
  };

  const closeTourEditModal = () => {
    setIsTourEditModalOpen(false);
    setEditingTour(null);
  };

  // Product editing functions
  const handleEditProduct = (product: Product) => {
    setEditingProduct(product);
    setIsProductEditModalOpen(true);
  };

  const handleSaveProduct = async (productId: number, data: any) => {
    try {
      const response = await fetch(`/api/admin/products/${productId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error("Failed to update product");
      }

      const updatedProduct = await response.json();
      setProducts(products.map(p => p.id === productId ? updatedProduct : p));

      toast({
        title: "Success",
        description: "Product updated successfully",
      });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : "Failed to update product";
      toast({
        title: "Error",
        description: errorMessage,
        variant: "destructive",
      });
      throw err;
    }
  };

  const closeProductEditModal = () => {
    setIsProductEditModalOpen(false);
    setEditingProduct(null);
  };

  if (!isAuthenticated || !isAdmin) {
    return null;
  }

  return (
