                            <TableCell className="font-medium">${tour.price}</TableCell>
                            <TableCell>{regions.find(r => r.id === tour.regionId)?.name || 'Unknown'}</TableCell>
                            <TableCell>
                              <Badge variant={tour.featured ? "default" : "secondary"}>
