import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Loader2 } from "lucide-react";

// Product type from schema
type Product = {
  id: number;
  name: string;
  nameKa: string | null;
  nameRu: string | null;
  description: string;
  descriptionKa: string | null;
  descriptionRu: string | null;
  price: string;
  category: string;
  imageUrl: string;
  inStock: boolean | null;
  featured: boolean | null;
};

// Product edit schema
const productEditSchema = z.object({
  // English fields
  name: z.string().min(1, "Product name is required"),
  description: z.string().min(1, "Description is required"),

  // Georgian fields
  nameKa: z.string().optional().or(z.literal("")),
  descriptionKa: z.string().optional().or(z.literal("")),

  // Russian fields
  nameRu: z.string().optional().or(z.literal("")),
  descriptionRu: z.string().optional().or(z.literal("")),

  // Other fields
  price: z.string().min(1, "Price is required"),
  category: z.string().min(1, "Category is required"),
  imageUrl: z.string().url("Invalid image URL"),
  inStock: z.boolean(),
  featured: z.boolean(),
});

type ProductEditData = z.infer<typeof productEditSchema>;

interface ProductEditFormProps {
  product: Product | null;
  isOpen: boolean;
  onClose: () => void;
  onSave: (productId: number, data: ProductEditData) => Promise<void>;
}

export function ProductEditForm({ product, isOpen, onClose, onSave }: ProductEditFormProps) {
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<ProductEditData>({
    resolver: zodResolver(productEditSchema),
    defaultValues: {
      name: product?.name || "",
      nameKa: product?.nameKa || "",
      nameRu: product?.nameRu || "",
      description: product?.description || "",
      descriptionKa: product?.descriptionKa || "",
      descriptionRu: product?.descriptionRu || "",
      price: product?.price || "",
      category: product?.category || "food",
      imageUrl: product?.imageUrl || "",
      inStock: product?.inStock ?? true,
      featured: product?.featured ?? false,
    },
  });

  // Reset form when product changes
  useEffect(() => {
    if (product) {
      form.reset({
        name: product.name || "",
        nameKa: product.nameKa || "",
        nameRu: product.nameRu || "",
        description: product.description || "",
        descriptionKa: product.descriptionKa || "",
        descriptionRu: product.descriptionRu || "",
        price: product.price || "",
        category: product.category || "food",
        imageUrl: product.imageUrl || "",
        inStock: product.inStock ?? true,
        featured: product.featured ?? false,
      });
    }
  }, [product, form]);

  const handleSubmit = async (data: ProductEditData) => {
    if (!product) return;

    setIsLoading(true);
    try {
      await onSave(product.id, data);
      onClose();
    } catch (error) {
      console.error("Failed to save product:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleClose = () => {
    form.reset();
    onClose();
  };

  if (!product) return null;

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Edit Product: {product.name}</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            <Tabs defaultValue="english" className="w-full">
              <TabsList className="grid grid-cols-3 w-full">
                <TabsTrigger value="english">English</TabsTrigger>
                <TabsTrigger value="georgian">Georgian</TabsTrigger>
