                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description *</FormLabel>
                      <FormControl>
                        <Textarea 
                          className="min-h-[120px]" 
                          {...field} 
                          data-testid="textarea-description" 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              {/* Georgian Tab */}
              <TabsContent value="georgian" className="space-y-4">
                <FormField
                  control={form.control}
                  name="nameKa"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Product Name (Georgian)</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-nameKa" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="descriptionKa"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description (Georgian)</FormLabel>
                      <FormControl>
                        <Textarea 
                          className="min-h-[120px]" 
                          {...field} 
                          data-testid="textarea-descriptionKa" 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              {/* Russian Tab */}
              <TabsContent value="russian" className="space-y-4">
                <FormField
                  control={form.control}
                  name="nameRu"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Product Name (Russian)</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-nameRu" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="descriptionRu"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description (Russian)</FormLabel>
                      <FormControl>
                        <Textarea 
                          className="min-h-[120px]" 
                          {...field} 
                          data-testid="textarea-descriptionRu" 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>
            </Tabs>

            {/* Product Details Section */}
            <div className="border-t pt-6">
              <h3 className="text-lg font-semibold mb-4">Product Details</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="price"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Price (USD) *</FormLabel>
                      <FormControl>
                        <Input 
                          type="number" 
                          step="0.01" 
                          {...field} 
                          data-testid="input-price" 
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="imageUrl"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Image URL *</FormLabel>
                      <FormControl>
                        <Input {...field} data-testid="input-imageUrl" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                <FormField
                  control={form.control}
                  name="inStock"
                  render={({ field }) => (
                    <FormItem className="flex items-center space-x-2 space-y-0">
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                          data-testid="switch-inStock"
                        />
                      </FormControl>
                      <FormLabel className="text-sm font-normal">
                        In Stock
                      </FormLabel>
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="featured"
                  render={({ field }) => (
                    <FormItem className="flex items-center space-x-2 space-y-0">
                      <FormControl>
                        <Switch
                          checked={field.value}
                          onCheckedChange={field.onChange}
                          data-testid="switch-featured"
                        />
                      </FormControl>
                      <FormLabel className="text-sm font-normal">
                        Featured Product
                      </FormLabel>
                    </FormItem>
                  )}
                />
              </div>
            </div>

            {/* Preview Section */}
            {product.imageUrl && (
              <div className="border-t pt-6">
                <h3 className="text-lg font-semibold mb-4">Current Product Image</h3>
                <img 
                  src={product.imageUrl} 
                  alt={product.name}
                  className="h-32 w-32 object-cover rounded-lg border"
                />
              </div>
            )}

            {/* Form Actions */}
            <div className="flex justify-end space-x-2 pt-4">
              <Button type="button" variant="outline" onClick={handleClose}>
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={isLoading}
                data-testid="button-save-product"
              >
                {isLoading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                Save Changes
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

