                />

                <FormField
                  control={form.control}
                  name="bestTimeToVisitKa"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Best Time to Visit (Georgian)</FormLabel>
                      <FormControl>
                        <Textarea {...field} rows={2} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="climateKa"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Climate (Georgian)</FormLabel>
                      <FormControl>
                        <Textarea {...field} rows={2} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="howToReachKa"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>How to Reach (Georgian)</FormLabel>
                      <FormControl>
                        <Textarea {...field} rows={2} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              {/* Russian Tab */}
              <TabsContent value="russian" className="space-y-4">
                <FormField
                  control={form.control}
                  name="nameRu"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Name (Russian)</FormLabel>
                      <FormControl>
                        <Input {...field} />
